import { APICallReturn } from "../types/types";
import * as sigUtil from "@metamask/eth-sig-util";
export default abstract class STKETHKeyEncrypter {
    abstract encrypt(data: string): Promise<APICallReturn<Uint8Array>>;
    abstract encryptWithPublicKey(data: string, publicKey: string): Promise<APICallReturn<Uint8Array>>;
    abstract getEncryptionPublicKey(): Promise<APICallReturn<string>>;
    abstract decrypt(data: Uint8Array): Promise<APICallReturn<string>>;
    abstract sign(data: sigUtil.TypedMessage<sigUtil.MessageTypes>): Promise<APICallReturn<{
        signature: string;
    }>>;
}
