import BackendService from "./index";

class ArweaveService extends BackendService {
    constructor(apiUrl: string) {
        super(apiUrl);
    }

    async getData(arweaveTxId: string) {
        const { data } = await this.triaAxios.get("/arweave/resolvetxid", {
            params: { arweaveTxId }
        })
        return data;
    }

}

export default ArweaveService;